import React, {useState, useEffect, useMemo} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Accordion from '@material-ui/core/Accordion';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Table from './Table';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    flexBasis: '33.33%',
    flexShrink: 0,
  },
  secondaryHeading: {
    fontSize: theme.typography.pxToRem(15),
    color: theme.palette.text.secondary,
  },
}));

export default function ControlledAccordions({data,props}) {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);
  const dates = data.date;
  const values = data.values;
  const [total, setTotal] = useState(0);

  useEffect(() => {
    calculateTotal();

  },[]);

  function calculateTotal(){
    var emissionTotal = 0;
    for (var i in values){

      emissionTotal = emissionTotal + parseInt(values[i].emission);
    }
    setTotal(emissionTotal)
  }

  const columns = useMemo(()=>[
    {
      Header: "name",
      accessor: "name"
    },{
      Header:"info",
      accessor:"info"
    },{
      Header: "Cost to the planet",
      accessor:"emission"
    }
  ])


  const handleChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
  };

  return (

    <div className={classes.root}>

      <Accordion expanded={expanded === dates} onChange={handleChange(dates)}>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1bh-content"
          id="panel1bh-header"
        >
          <Typography className={classes.heading}>{dates}</Typography>
          <Typography className={classes.secondaryHeading}>Total Daily Footprint: {total}</Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Table columns={columns} data={values} />
        </AccordionDetails>
      </Accordion>

    </div>
  );
}
