import React, {useMemo, useEffect, useState} from "react";
import Table from './Table';
import ControlledAccordions from './Accordion';

function CarbonDisplay(props){
	const testdata = require('./Resources/testdata.json');
	const results = useState(testdata['data']);
	const [data,setData] = useState([]);

	const columns = useMemo(()=>[
		{
			Header: "date",
			accessor: "date"
		},{
			Header:"name",
			accessor:"name"
		},{
			Header: "Cost to the planet",
			accessor:"impact"
		}
		])


	return(<>

		<h1>Tahmid's Carbon Tracker</h1>
		<div className="container">
		<ControlledAccordions data = {data}/>
		<Table columns={columns} data={data}/>
	</div>
	</>)

}

export default CarbonDisplay;
