import React, {useMemo, useEffect, useState} from "react";
import Table from './Table';
import ControlledAccordions from './Accordion';
import axios from 'axios';

function CarbonDisplay(props){
	const testdata = require('./Resources/testdata.json');
	const results = useState(testdata['data']);
	const [data,setData] = useState([]);

	const columns = useMemo(()=>[
		{
			Header: "date",
			accessor: "date"
		},{
			Header:"name",
			accessor:"name"
		},{
			Header: "Cost to the planet",
			accessor:"impact"
		}
	])

	let axiosConfig = {
		headers: {
			'x-identity':'test'
		}
	};

	useEffect(() => {
		setData(results);
		(async () => {
			await getEmissionsData();
		})().catch(error => {
			console.log(error);
		});
	},[]);

	async function getEmissionsData(){
		await axios.get('https://carbon-tracker-ec6o7ozhzq-uc.a.run.app/footprint', axiosConfig).then(response => {
			console.log(response);
			var holdData = {};
			var values = []
			var data = response.data.data;
			for (var i in data){
				Object.values(data[i]).map((k,v) => {
					console.log(k);
					holdData['date'] = data[i];
					holdData['values'] = k;

				})
			}
		setData(holdData);


		}).catch(error => {
			console.log(error);
		})
	}


	return(<>

		<h1>Tahmid's Carbon Tracker</h1>
		<div className="container">
		<ControlledAccordions data = {data}/>

	</div>
	</>)

}

export default CarbonDisplay;
